import javax.inject.Inject
import javax.inject.Singleton
import retrofit2.Retrofit
import retrofit2.http.GET
import retrofit2.http.Header
import retrofit2.http.Path

interface MiniAppApi {
    @GET(value = "v3/videos/{videoId}")
    suspend fun getTrendingMovies(
        @Path("videoId") id: String = "0_n8rvvqmy",
        @Header("webPlatform")
        webPlatform: String = "878a6db06e0cd079b3b02408d246801d217c018f",
        @Header("ChannelID")
        VMP: String = "VMP",
        @Header("TRANSACTIONID")
        TRANSACTIONID: String = "A908111111111111"
    ): NetworkPayloadResponse<Movies>
}

@Singleton
